contract ExchangeDomainV1 {
    enum AssetType {
        NO_USE,
        ETH,
        ERC20,
        ERC1155,
        ERC721,
        ERC721Deprecated
    }

    struct Asset {
        address token;
        uint256 tokenId;
        AssetType assetType;
    }

    struct OrderKey {
        /* who signed the order */
        address owner;
        /* random number */
        uint256 salt;
        /* what has owner */
        Asset sellAsset;
        /* what wants owner */
        Asset buyAsset;
    }

    struct Order {
        OrderKey key;
        /* how much has owner (in wei, or UINT256_MAX if ERC-721) */
        uint256 selling;
        /* how much wants owner (in wei, or UINT256_MAX if ERC-721) */
        uint256 buying;
    }

    /* An ECDSA signature. */
    struct Sig {
        /* v parameter */
        uint8 v;
        /* r parameter */
        bytes32 r;
        /* s parameter */
        bytes32 s;
    }
}

contract ExchangeV1 is Ownable, ExchangeDomainV1 {
    using SafeMath for uint256;
    using UintLibrary for uint256;
    using StringLibrary for string;
    using BytesLibrary for bytes32;

    event Buy(
        address indexed sellToken,
        uint256 indexed sellTokenId,
        uint256 sellValue,
        address owner,
        address buyToken,
        uint256 buyTokenId,
        uint256 buyValue,
        address buyer,
        uint256 amount,
        uint256 salt
    );

    // 平台手续费占比及钱包账号
    uint256 public platform_ratio = 250; // 2.5% * 100
    address payable public platform_account = address(0);
    // 作者手续费占比及钱包账号
    uint256 public author_ratio = 500; // 5% * 100
    address payable public author_account = address(0);
    // 受益人手续费占比及钱包账号
    uint256 public beneficiary_ratio = 500; // 5% * 100
    address payable public beneficiary_account = address(0);
    // 组合分成占比
    uint256 public relation_ratio = 500; // 5% * 100

    function setPlatformRatio(uint256 _ratio) external onlyOwner {
        platform_ratio = _ratio;
    }

    function setPlatformAccount(address payable _account) external onlyOwner {
        platform_account = _account;
    }

    function setRelationRatio(uint256 _ratio) external onlyOwner {
        relation_ratio = _ratio;
    }

    function setAuthorRatio(uint256 _ratio) external onlyOwner {
        author_ratio = _ratio;
    }

    function setAuthorAccount(address payable _account) external onlyOwner {
        author_account = _account;
    }

    function setBeneficiaryRatio(uint256 _ratio) external onlyOwner {
        beneficiary_ratio = _ratio;
    }

    function setBeneficiaryAccount(address payable _account)
        external
        onlyOwner
    {
        beneficiary_account = _account;
    }

    function validateOrderSig(Order memory order, Sig memory sig)
        internal
        pure
    {
        if (sig.v == 0 && sig.r == bytes32(0x0) && sig.s == bytes32(0x0)) {
            require(false, "incorrect signature");
        } else {
            require(
                prepareMessage(order).recover(sig.v, sig.r, sig.s) ==
                    order.key.owner,
                "incorrect signature"
            );
        }
    }

    function prepareMessage(Order memory order)
        public
        pure
        returns (string memory)
    {
        return keccak256(abi.encode(order)).toString();
    }

    function erc721safeTransferFrom(
        IERC721 token,
        address from,
        address to,
        uint256 tokenId
    ) internal {
        token.safeTransferFrom(from, to, tokenId);
    }

    function erc20safeTransferFrom(
        IERC20 token,
        address from,
        address to,
        uint256 value
    ) internal {
        require(
            token.transferFrom(from, to, value),
            "failure while transferring"
        );
    }

    function exchange(
        Order calldata order,
        Sig calldata sig,
        uint256 amount,
        address buyer
    ) external payable {
        validateOrderSig(order, sig);
        require(
            order.key.sellAsset.assetType != AssetType.ETH,
            "ETH is not supported on sell side"
        );
        require(order.key.owner != msg.sender, "owner is not self");
        if (buyer == address(0)) {
            buyer = msg.sender;
        }
        if (order.key.sellAsset.assetType > order.key.buyAsset.assetType) {
            require(msg.value >= order.buying, "buying value is low");
            require(order.selling == 1, "selling is not one");
            // 直接购买
            // 721转账
            transfer(order.key.sellAsset, amount, order.key.owner, buyer);
            // bnb转账
            transferWithFees(
                order.key.buyAsset,
                order.buying,
                msg.sender,
                order.key.owner,
                order.key.sellAsset
            );
        } else {
            require(order.selling > 0, "selling value is low");
            require(order.buying == 1, "buying is not one");
            // 竞拍交易
            // 721转账
            transfer(order.key.buyAsset, order.buying, buyer, order.key.owner);
            // wbnb转账
            transferWithFees(
                order.key.sellAsset,
                order.selling,
                order.key.owner,
                msg.sender,
                order.key.buyAsset
            );
        }

        emitBuy(order, amount, buyer);
    }

    function transfer(
        Asset memory asset,
        uint256 value,
        address from,
        address to
    ) internal {
        if (asset.assetType == AssetType.ETH) {
            address payable toPayable = address(uint160(to));
            toPayable.transfer(value);
        } else if (asset.assetType == AssetType.ERC20) {
            require(asset.tokenId == 0, "tokenId should be 0");
            erc20safeTransferFrom(IERC20(asset.token), from, to, value);
        } else if (asset.assetType == AssetType.ERC721) {
            require(value == 1, "value should be 1 for ERC-721");
            erc721safeTransferFrom(
                IERC721(asset.token),
                from,
                to,
                asset.tokenId
            );
        } else {
            require(false, "incorrect asset");
        }
    }

    function transferWithFees(
        Asset memory inAsset,
        uint256 value,
        address from,
        address to,
        Asset memory outAsset
    ) internal {
        require(
            outAsset.assetType == AssetType.ERC721,
            "assetType must ERC721"
        );
        IERC721 erc721 = IERC721(outAsset.token);
        uint256 _kind = erc721.getKind(outAsset.tokenId);
        // 平台费 2.5%
        uint256 restValue = value.sub(_platformFee(inAsset, value, from));
        // 原创作者 5%
        restValue = restValue.sub(_authorFee(inAsset, value, from));
        // 受益人费用 5%
        restValue = restValue.sub(_beneficiaryFee(inAsset, value, from));

        if (_kind == 2) {
            // 分成费用 5%
            uint256[] memory relTokenIds = erc721.getRelations(
                outAsset.tokenId
            );
            uint256 relLen = relTokenIds.length;
            require(relLen > 0);
            uint256 _feeOne = _relationFeeOne(value, relLen);
            for (uint256 i = 0; i < relLen; i++) {
                address _owner = erc721.ownerOf(relTokenIds[i]);
                _relationFee(inAsset, _feeOne, from, _owner);
                restValue = restValue.sub(_feeOne);
            }
        }
        address payable toPayable = address(uint160(to));
        transfer(inAsset, restValue, from, toPayable);
    }

    function _relationFeeOne(uint256 value, uint256 len)
        internal
        view
        returns (uint256)
    {
        uint256 _fee = value.mul(relation_ratio).div(10000);
        return _fee.div(len);
    }

    // 平台费
    function _platformFee(
        Asset memory inAsset,
        uint256 value,
        address from
    ) internal returns (uint256) {
        require(value > 0, "platform fee value is zero");
        require(platform_account != address(0), "platform account is zero");
        uint256 _fee = value.mul(platform_ratio).div(10000);
        transfer(inAsset, _fee, from, platform_account);
        return _fee;
    }

    // 原创作者费
    function _authorFee(
        Asset memory inAsset,
        uint256 value,
        address from
    ) internal returns (uint256) {
        require(value > 0, "author fee value is zero");
        require(author_account != address(0), "platform account is zero");
        uint256 _fee = value.mul(author_ratio).div(10000);
        transfer(inAsset, _fee, from, author_account);
        return _fee;
    }

    // 受益人费用
    function _beneficiaryFee(
        Asset memory inAsset,
        uint256 value,
        address from
    ) internal returns (uint256) {
        require(value > 0, "beneficiary fee value is zero");
        require(
            beneficiary_account != address(0),
            "beneficiary account is zero"
        );
        uint256 _fee = value.mul(beneficiary_ratio).div(10000);
        transfer(inAsset, _fee, from, beneficiary_account);
        return _fee;
    }

    // 关联分成费
    function _relationFee(
        Asset memory inAsset,
        uint256 value,
        address from,
        address to
    ) internal returns (bool) {
        require(value > 0, "relation fee value is zero");
        require(to != address(0), "relation account to is zero");
        transfer(inAsset, value, from, to);
        return true;
    }

    function emitBuy(
        Order memory order,
        uint256 amount,
        address buyer
    ) internal {
        emit Buy(
            order.key.sellAsset.token,
            order.key.sellAsset.tokenId,
            order.selling,
            order.key.owner,
            order.key.buyAsset.token,
            order.key.buyAsset.tokenId,
            order.buying,
            buyer,
            amount,
            order.key.salt
        );
    }

    function destroy() external onlyOwner {
        selfdestruct(msg.sender);
    }
}
